 generate """ self . _name = name self . _description = description self . _transport = transport self . _vector_db = vector_db self . _collection_name = collection_name self . _k = k self . _num_verification_questions = num_verification_questions llm_agent = llm super () . __init__ ( name = name , description = description , transport = self . transport , llm = llm_agent , vector_db = vector_db , collection_name = collection_name ) def __repr__ ( self ): """Return a string representation of the agent.""" return ( f " { self . __class__ . __name__ } (name= { self . name } , description= { self . description } , llm= { self . llm } ," f "vector_db= { self . vector_db } , collection_name= { self . collection_name } , k= { self . k } ," f "num_verification_questions= { self . _num_verification_questions } , transport= { self . transport } )" ) def process_event ( self , event ): """Do nothing""" return True def process_error ( self , error ): """Log the error and return True.""" logging . error ( error ) return True def process_request ( self , request : WiseAgentMessage ): """ Process a message containing a question and a baseline response to the question by challenging the baseline response to generate a revised response to the original question. Args: request (WiseAgentMessage): the request message to process """ llm_response = self . _create_and_process_chain_of_verification_prompts ( request . message ) self . send_response ( WiseAgentMessage ( llm_response , self . name ), request . sender ) return True def process_response ( self , response : WiseAgentMessage ): """Do nothing""" return True def get_recipient_agent_name ( self , message ): """Return the name of the agent to send the message to.""" return self . name def stop ( self ): """Do nothing""" pass @property def name ( self ) -> str : """Get the name of the agent.""" return self . _name @property def k ( self ) -> int : """Get the number of documents to retrieve from the vector database.""" return self . 